import React, { Component } from 'react';
import 'semantic-ui-css/semantic.min.css';
import {
  Image,
  Button,
  Card,
  Feed,
  Segment
} from 'semantic-ui-react';
import './Item.css'

class Items extends Component {

  constructor(){
      super();
      this.state = {
          items: []
      }
  }

  componentDidMount = () =>{
      fetch('api/items')
        .then(res => res.json())
        .then(items => this.setState({items}, () => console.log('Items Fetched: ', items)));
  }

  render() {
    return (
      <div>
          <h2>Items</h2>
          <Feed>
              <Segment style={{fontSize: '16px'}}>
              {this.state.items.map(item =>
                 <Feed.Event>
                   <Feed.Label style={{height: '150px', width: '150px'}}><Image id='item' avatar size='large' src={item.imageURL} /> </Feed.Label>
                   <Feed.Content>
                     <Feed.Date content={item.price} />
                     <Feed.Summary>
                       {item.name}
                     </Feed.Summary>
                   </Feed.Content>
                 </Feed.Event>
            )}
            </Segment>
          </Feed>
    </div>
    );
  }
}

export default Items;
