import React, { Component } from 'react';
import 'semantic-ui-css/semantic.min.css';
import {
  Image,
  Button,
  Card,
  Feed
} from 'semantic-ui-react'

class Items extends Component {

  constructor(){
      super();
      this.state = {
          items: []
      }
  }

  componentDidMount = () =>{
      fetch('api/items')
        .then(res => res.json())
        .then(items => this.setState({items}, () => console.log('Items Fetched: ', items)));
  }

  render() {
    return (
      <div>
          <h2>Items</h2>
          <Feed>
              {this.state.items.map(item =>
                 <Feed.Event>
                   <Feed.Label style={{height: '150px', width: '150px', borderRadius: '12px !important'}}><Image avatar size='large' src='/static/media/hat_MH_NBA.2516f43c.jpeg' /> </Feed.Label>
                   <Feed.Content>
                     <Feed.Date content='1 day ago' />
                     <Feed.Summary>
                       You added <a>Jenny Hess</a> to your <a>coworker</a> group.
                     </Feed.Summary>
                   </Feed.Content>
                 </Feed.Event>
            )}
          </Feed>
    </div>
    );
  }
}

export default Items;
